package Strings;

public class WrapperClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s="hijit";
        Character ch=s.charAt(0);
        if(Character.isUpperCase(ch)){
            System.out.println("True");
        }
        else{
            System.out.println("False");
        }

	}

}
